import React, { useState } from 'react';
import { addReusableCupData, addDisposableCupData } from './backend';
import './vendorPage.css';

function VendorIP() {
  const [name, setName] = useState('');
  const [reusableCups, setReusableCups] = useState(0);
  const [nonReusableCups, setNonReusableCups] = useState(0);

  const handleSubmit = (e) => {
    e.preventDefault();
    addReusableCupData(name, reusableCups);
    addDisposableCupData(name, nonReusableCups);
    alert(`Data submitted for ${name}`);
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h1>Reusable vs Non-Reusable Cups</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>
            Customer Id:
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </label>
        </div>

        <div>
          <label>
            Reusable Cups:
            <input
              type="number"
              value={reusableCups}
              onChange={(e) => setReusableCups(e.target.value)}
              min="0"
              required
            />
          </label>
        </div>

        <div>
          <label>
            Non-Reusable Cups:
            <input
              type="number"
              value={nonReusableCups}
              onChange={(e) => setNonReusableCups(e.target.value)}
              min="0"
              required
            />
          </label>
        </div>

        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default VendorIP;
