import React from "react";
import { redeemCoffee } from './backend'; // Import the redeemCoffee function
import './studentHeader.css';

export default function Header() {
  const handleRedeem = () => {
    const studentEmail = "student1@example.com"; // Replace with the logged-in student's email
    if (redeemCoffee(studentEmail)) {
      alert("You have successfully redeemed a coffee!");
      window.location.reload(); // Refresh the page to update points
    } else {
      alert("You do not have enough points to redeem a coffee.");
    }
  };

  return (
    <header className="header-container">
      <div className="header-content">
        <h1 className="header-title">Eco Cup Tracker</h1>
        <nav className="header-nav">
          <a href="#home" className="nav-link">
            Home
          </a>
          <a href="#ranking" className="nav-link">
            Ranking
          </a>
          <a href="#community" className="nav-link">
            Community
          </a>
          <button onClick={handleRedeem} className="nav-link">
            Redeem
          </button>
        </nav>
      </div>
    </header>
  );
}
