// backend.js
export const users = [
  { id: 1, type: "student", email: "student@example.com", password: "student123" },
  { id: 2, type: "vendor", email: "vendor@example.com", password: "vendor123" },
  { id: 3, type: "admin", email: "admin@example.com", password: "admin123" },
];

export const reusableCupsData = []; // Stores reusable cup usage data
export const disposableCupsData = []; // Stores disposable cup usage data

// Function to authenticate user
export const authenticateUser = (email, password, userType) => {
  const user = users.find(
    (u) => u.email === email && u.password === password && u.type === userType
  );
  return user ? user : null;
};

// Function to add reusable cup data
export const addReusableCupData = (userId, cupsUsed) => {
  reusableCupsData.push({ userId, cupsUsed, timestamp: new Date() });
};

// Function to add disposable cup data
export const addDisposableCupData = (userId, cupsUsed) => {
  disposableCupsData.push({ userId, cupsUsed, timestamp: new Date() });
};
